/**
 * Class to handle webcam
 */
export const Webcam = /*#__PURE__*/(() => {
  class Webcam {
    constructor() {
      /**
       * Open webcam and stream it through video tag.
       * @param {HTMLVideoElement} videoRef video tag reference
       */
      this.open = videoRef => {
        if (window.navigator.mediaDevices && window.navigator.mediaDevices.getUserMedia) {
          window.navigator.mediaDevices.getUserMedia({
            audio: false,
            video: {
              facingMode: "environment"
            }
          }).then(stream => {
            videoRef.srcObject = stream;
          });
        } else window.alert("Can't open Webcam!");
      };
      /**
       * Close opened webcam.
       * @param {HTMLVideoElement} videoRef video tag reference
       */
      this.close = videoRef => {
        if (videoRef.srcObject) {
          videoRef.srcObject.getTracks().forEach(track => {
            track.stop();
          });
          videoRef.srcObject = null;
        } else window.alert("Please open Webcam first!");
      };
    }
  }
  return Webcam;
})();
